(()=>{"use strict";const t=t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");(()=>{let s=(()=>{let e=(new Date(t).getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,hours:Math.floor(e/3600),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();e.textContent=s.hours,n.textContent=s.minutes,o.textContent=s.seconds,s.timeRemaining>0?(s.hours<10&&(e.textContent="0"+s.hours),s.minutes<10&&(n.textContent="0"+s.minutes),s.seconds<10&&(o.textContent="0"+s.seconds)):(e.textContent="00",n.textContent="00",o.textContent="00")})()};setInterval(t,1e3,"30 october 2021"),t("30 october 2021")})();